void Configuracoes_ADS1198ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &voltar)
    {
        //InteractionVoltar
        //When voltar clicked change screen to Configuracoes
        //Go to Configuracoes with no screen transition
        application().gotoConfiguracoesScreenNoTransition();
    }
    else if (&src == &ConfigtoExame)
    {
        //InteractionConfigToExame
        //When ConfigtoExame clicked change screen to Screen1
        //Go to Screen1 with no screen transition
        application().gotoScreen1ScreenNoTransition();

        //InteractionStartRDataC
        //When ConfigtoExame clicked call virtual function
        //Call StartRDataC
        StartRDataC();
    }
}

