void NovoPacienteViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &voltar)
    {
        //InteractionVoltar
        //When voltar clicked change screen to Cadastro
        //Go to Cadastro with no screen transition
        application().gotoCadastroScreenNoTransition();
    }
    else if (&src == &buttonSalvarCadastro)
    {
        //CliqueSalvarCadastro
        //When buttonSalvarCadastro clicked call virtual function
        //Call SalvarCadastro
        SalvarCadastro();
    }
    else if (&src == &buttonSalvarDados)
    {
        //SalvarDadosTeclado
        //When buttonSalvarDados clicked call virtual function
        //Call SalvarDadosTeclado
        SalvarDadosTeclado();
    }
    else if (&src == &buttonLimparDados)
    {
        //LimpaDadosDosBuffers
        //When buttonLimparDados clicked call virtual function
        //Call LimpaDados
        LimpaDados();
    }
    else if (&src == &buttonInfoSalvar)
    {
        //InformacoesSalvarServidor
        //When buttonInfoSalvar clicked call virtual function
        //Call InfoSalvar
        InfoSalvar();
    }
}

